        console.log(`añadiste ${numero}`);
        setShow(false);
    };

    return (
        <div className="container-detail">
            <img src={item.img} alt="" />
            <div>
                <h2>{item.title}</h2>
                <p></p>
                <ItemCount prueba={prueba} stock={10} initial={1} />
                {(
                    <button>Ir al carrito</button>
