import {addDoc,collection,documentId,getDocs,query,serverTimestamp,where,writeBatch,} from 'firebase/firestore';
import { useContext, useState } from 'react';
import { CartContext } from '../../Context/CartContext';
import { database} from '../../services/firebaseConfig';
const Form = () => {
    const [nombre, setName] = useState('');
    const [lastName, setLastName] = useState('');
    const [gmail, setGmail] = useState('');
    const [loading, setLoading] = useState(false);

    const [orderId, setOrderId] = useState('');

    const { cart, total, deleteAll } = useContext(CartContext);

    const totalPrice = total();

    const handleSubmit = async (e) => {
        setLoading(true);
        e.preventDefault();

        try {
            const order = {
                buyer: { nombre, lastName },
                items: cart,
                total: totalPrice,
                date: serverTimestamp(),
            };

            const ids = cart.map((prod) => prod.id);

            const productsRef = collection(database, 'productos');

            const productsAddedFromFirestore = await getDocs(
                query(productsRef, where(documentId(), 'in', ids))
            );

            const { docs } = productsAddedFromFirestore;

            const outOfStock = [];

            const batch = writeBatch(database);

            docs.forEach((doc) => {
                const dataDoc = doc.data();
                const stockDB = dataDoc.stock;

                const productAddedToCart = cart.find(
                    (prod) => prod.id === doc.id
                );
                const prodQuantity = productAddedToCart?.cantidad;
                if (stockDB >= prodQuantity) {
                    batch.update(doc.ref, { stock: stockDB - prodQuantity });
                } else {
                    outOfStock.push({ id: doc.id, ...dataDoc });
                }
            });
            if (outOfStock.length === 0) {
                batch.commit();
                const orderRef = collection(database, 'orders');
                const orderAdded = await addDoc(orderRef, order);
                setOrderId(orderAdded.id);
                deleteAll();
            } else {
                console.log('No hay stock de algún producto');
            }
        } catch (error) {
            console.log(error);
        } finally {
            setLoading(false);
        }
    };
    const handleChangeName = (e) => {
        setName(e.target.value);
    };

    const handleChangeLastName = (e) => {
        setLastName(e.target.value);
    };
    const handleChangeGmail = (e) => {
        setGmail(e.target.value);
    };
    if (orderId) {
        return (
            <h1 className='form'>
                Muchas gracias por tu compra en 3DLocuras,tu pedido llegara pronto.Puedes tranquearlo usando el siguiente codigo:${orderId}
            </h1>
        );
    }

    return (
        <div className='formnames'>
            <form onSubmit={handleSubmit} action="">
                <input
                    type="text"
                    name="nombre"
                    placeholder="Nombre..."
                    onChange={handleChangeName}
                    value={nombre}
                />
                <input
                    type="text"
                    name="apellido"
                    placeholder="Apellido..."
                    onChange={handleChangeLastName}
                    value={lastName}
                />
                <input
                    type="gmail"
                    name="Gmail"
                    placeholder="Gmail..."
                    onChange={handleChangeGmail}
                    value={gmail}
                />
                <button>{loading ? 'Enviando...' : 'Enviar'}</button>
            </form>
        </div>
    );
};

export default Form;
